$('#ueberweisungButton').click(function(){
    $('#konto').val($('#exampleInputKonto').val());
    $('#empfaenger').val($('#exampleInputEmpfaenger').val());
    $('#iban').val($('#exampleInputIban').val());
    $('#betrag').val($('#exampleInputBetrag').val());
    $('#verwendung').val($('#exampleInputVerwendung').val());
    $('#ausfuehrung').val($('#exampleInputAusfuehrung').val());
});

